        return 60
    }
    
    func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -> UITableViewCell {
        let cell = tableView.dequeueReusableCell(withIdentifier: "SecondTableViewCell", for: indexPath) as? SecondTableViewCell
        cell?.lblTitle.text = titles[indexPath.row]
        
        switch indexPath.row {
        case 0:
            cell?.arrowImg.image = UIImage(named: "down")
            cell?.cirImg.image = UIImage(named: "recycle")
            cell?.otDescLbl.isHidden = true
        case 1:
            cell?.arrowImg.image = UIImage(named: "down")
            cell?.cirImg.image = UIImage(named: "recycle")
            cell?.descLbl.text = "Jeden Di. und Do. um 16:00"
            cell?.otDescLbl.isHidden = true
        case 2:
            cell?.cirImg.image = UIImage(named: "circle")
            cell?.descLbl.text = "Fressnapf XXL München"
            cell?.otDescLbl.isHidden = true
        case 3:
            cell?.cirImg.image = UIImage(named: "circle")
            cell?.descLbl.isHidden = true
            cell?.otDescLbl.isHidden = true
        case 4:
            cell?.arrowImg.image = UIImage(named: "next")
            cell?.descLbl.isHidden = true
            cell?.otDescLbl.text = "Alle anzeigen"
        case 5:
            cell?.cirImg.image = UIImage(named: "circle")
            cell?.descLbl.isHidden = true
            cell?.otDescLbl.isHidden = true
        case 6:
            cell?.cirImg.image = UIImage(named: "circle")
            cell?.descLbl.isHidden = true
            cell?.otDescLbl.isHidden = true
        default:
            print("NO SE ENCONTRO ASSET")
        }
        return cell!
    }
    
    func tableView(_ tableView: UITableView, didSelectRowAt indexPath: IndexPath) {
        tableView.deselectRow(at: indexPath, animated: true)
        print(indexPath.row)
        let cell = self.tableView.dequeueReusableCell(withIdentifier: "SecondTableViewCell") as? SecondTableViewCell
        if (indexPath.row == 0){
            
            //sender.setTitle(item, for: .normal) //9
            guard let currentCell = tableView.cellForRow(at: indexPath) else { return }
            setupGenderDropDown(cell: currentCell as! SecondTableViewCell)
            
            
        }
        if (indexPath.row == 1){
            
            guard let currentCell = tableView.cellForRow(at: indexPath) else { return }
            powerGassi(cell: currentCell as! SecondTableViewCell)
            
            
        }
        if (indexPath.row == 2){
            
            guard let currentCell = tableView.cellForRow(at: indexPath) else { return }
            Neuen(cell: currentCell as! SecondTableViewCell)
            
            
        }
        if (indexPath.row == 3){
            
            guard let currentCell = tableView.cellForRow(at: indexPath) else { return }
            Fur(cell: currentCell as! SecondTableViewCell)
            
            
        }
        if (indexPath.row == 4){
            
            let vc = UIStoryboard.init(name: "Main", bundle: Bundle.main).instantiateViewController(withIdentifier: "AllTaskViewController") as? AllTaskViewController
            self.navigationController?.pushViewController(vc!, animated: true)
            
            
        }
        
        
